import React from 'react';
import { useState } from 'react';

export default function Textnote(props) {

    const [text, setText] = useState('Enter the comment');
    // setText('Eenter new value');


    
    return (
        <div className='container mt-5 pt-5'>
            <form>
                <h4> {props.title}</h4>
                <div className="form-group">
                    <input type="email" className="form-control" id="form1" />
                </div>

                <div className="form-group">

                    <textarea className="form-control" value={text} id="formarea" rows="3"></textarea>
                </div>
                <button type="submit" onClick={handleUpCase} class="btn btn-primary">Convert Uppoer Case</button>
                <button type="submit" class="btn btn-primary">Reset</button>

            </form >
        </div >
    )

}
